<template>

  <HomeDropdown />
  
  <div class="main">
    <div id='stars'></div>
    <div id='stars2'></div>
    <div id='stars3'></div>

    <div class="text-3xl min-h-screen flex justify-center items-center">
      <div class="text-center w-[95%] md:w-1/2 " ref="phrase"></div>
    </div>
  </div>
</template>

<script setup>
import { ref, onMounted, onBeforeMount, watch } from 'vue';
import TypeIt from 'typeit';
import HomeDropdown from '../components/HomeDropdown.vue';
import publicUseStore from '../stores/publicStore';

const phrase = ref(null);

const store = publicUseStore();

const data = ref([])

onBeforeMount(() => {
  store.getPhrases(5, store.lang).then((res) => {
    data.value = res.phrases;
  })

}),

  onMounted(() => {
    let currentIndex = ref(0);

    const typeNextPhrase = () => {
      const currentPhrase = data.value[currentIndex.value].phrase;
      const currentBook = data.value[currentIndex.value].author;
      new TypeIt(phrase.value, {
        waitUntilVisible: true,
        speed: 50,
        cursor: false
      })
        .type(currentPhrase)
        .pause(1000)
        .type(`<div class="text-end mt-12">${currentBook}</div>`)
        .pause(5000)
        .delete(currentBook.length + 2 + currentPhrase.length)
        .exec(() => {
          currentIndex.value = (currentIndex.value + 1) % data.value.length;
          typeNextPhrase();
        })
        .go();
    };

    watch(data, (newValue) => {
      if (newValue.length > 0) {
        typeNextPhrase();
      }
    });

  });

</script>

